<?phpnamespace App\Http\Controllers;use App\Model\Carousel;use Illuminate\Http\Request;class CarouselController extends CommonController{    public function carousel(Request $request)    {        if ($request->isMethod('POST')) {            $data = $request->input();            $img = $request->file('image');            if ($img->isValid()) {                //源文件名                $originalName = $img->getClientOriginalName();                $path = public_path('upload');                $img->move($path, $originalName);                $carousel = new Carousel();                $carousel->carousel_name = $data['carousel_name'];                $carousel->carousel_path = $originalName;                $carousel->sort = $data['sort'];                $carousel->is_show = $data['is_show'];                if ($carousel->save()) {                    $id = $carousel->carousel_id;                    $carouselList = Carousel::find($id)->toArray();                    $result = [                        'success' => 1,                        'data'    => $carouselList,                    ];                    echo json_encode($result);exit;                }            }        }        $carousel = Carousel::all()->toArray();        return view('carousel.carousel', ['carousel' => $carousel]);    }    public function delete(Request $request)    {        $id = $request->get('id');        $bool = Carousel::where('carousel_id', '=', $id)->delete();        if ($bool) {            echo 1;exit;        } else {            echo 0;exit;        }    }}