<?phpnamespace App\Http\Controllers;use frontend\models\Reg;use Illuminate\Http\Request;use Illuminate\Support\Facades\Session;use App\Model\Admin;class RegisterController extends Controller{    public function register(Request $request)    {        if ($request->isMethod('POST')) {            $this->validate($request, [                'username' => 'required|min:2|max:20',                'password_confirmation' => 'required|between:6,32',                'password' => 'required|between:6,32|confirmed',            ], [                'required' => ':attribute 为必填项',                'min' => ':attribute 长度不符合要求',                'max' => ':attribute 长度不符合要求',                'confirmed' => ':attribute 不相同',            ], [                'username' => '姓名',                'password' => '密码',                'password_confirmation' => '确认密码',            ]);            $data = $request->input();            $admin = new Admin();            $admin->admin_name = $data['username'];            $admin->admin_password = md5(md5($data['password'], true));            if ($admin->save()) {                return redirect('loginLogin');            } else {                return redirect('registerRegister');            }        }        return view('register.register');    }}