<?phpnamespace App\Http\Controllers;use DB;use App\Model\Type;use Illuminate\Http\Request;class TypeController extends CommonController{    //品牌展示    public function show()    {        $typeList = Type::paginate(3);        return view('type.show', ['typeList' => $typeList]);    }    //品牌添加    public function add(Request $request)    {        if ($request->isMethod('POST')) {            $data = $request->input();            $admin = new Type();            $admin->type_name = $data['type_name'];            if ($admin->save()) {                return redirect('typeShow')->with('success', '添加成功');            } else {                return redirect('typeAdd')->with('error', '添加失败');            }                    }                return view('type.add');    }    //品牌修改    public function edit(Request $request)    {        if ($request->isMethod('POST')) {            $data = $request->input();            $admin = Type::find($data['type_id']);            $admin->type_name = $data['type_name'];            if ($admin->save()) {                return redirect('typeShow')->with('success', '修改成功');            } else {                return redirect('typeEdit')->with('error', '修改失败');            }        }        $type_id = $request->get('id');        $typeList = Type::find($type_id)->toArray();        return view('type.edit', ['typeList' => $typeList]);    }    //品牌删除    public function delete(Request $request)    {        $type_id = $request->get('id');        $bool = Type::where('type_id', '=', $type_id)->delete();        if ($bool) {            return redirect('typeShow')->with('success', '删除成功');        } else {            return redirect('typeShow')->with('error', '删除失败');        }    }}