<?phpnamespace App\Model;use Illuminate\Database\Eloquent\Model;class Node extends Model{    protected $table = 'node';    public $primaryKey = 'node_id';    public $timestamps = false;    public function nodeData($data, $parent_id = 0, $level = 0)    {        $arr = [];        foreach($data as $val) {            if ($val['parent_id'] == $parent_id) {                $val['level'] = $level;                $val['child'] = $this->nodeData($data, $val['node_id'], $level + 1);                $arr[] = $val;            }        }        return $arr;    }}