<!DOCTYPE html><html><head>    <meta charset="utf-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title> - 基本表单</title>    <meta name="keywords" content="">    <meta name="description" content="">    <link rel="shortcut icon" href="favicon.ico"> <link href="css/bootstrap.min.css?v=3.3.6" rel="stylesheet">    <link href="css/font-awesome.css?v=4.4.0" rel="stylesheet">    <link href="css/plugins/iCheck/custom.css" rel="stylesheet">    <link href="css/animate.css" rel="stylesheet">    <link href="css/style.css?v=4.1.0" rel="stylesheet"></head><body class="gray-bg"><div class="wrapper wrapper-content animated fadeInRight">    <div class="row">        <div class="col-sm-12">            <div class="ibox-content" id="carousel">                    <table class="table">                    <tr>                        <th>名称</th>                        <th>图片</th>                        <th>排序</th>                        <th>是否显示</th>                        <th>操作</th>                    </tr>                    <tbody id="tbody">                    @foreach($carousel as $val)                    <tr>                        <td>{{ $val['carousel_name'] }}</td>                        <td><img src="upload/{{ $val['carousel_path'] }}" alt="" style="height: 118px;width: 200px"></td>                        <td>{{ $val['sort'] }}</td>                        <td alt="{{ $val['carousel_id'] }}">                            @if ($val['is_show'] == 1)                                <img src="img/yes.gif" class="image">                            @else                                <img src="img/no.gif" class="image">                            @endif                        </td>                        <td><a href="javascript:void(0)" onclick="if (confirm('确实要删除吗？') == false) return false" id="delete" alt="{{ $val['carousel_id'] }}">删除</a></td>                    </tr>                    @endforeach                    </tbody>                    </table>            </div>            <div class="ibox float-e-margins">                <div class="ibox-title">                    <h5>轮播图添加</h5>                </div>                <div class="ibox-content">                    <form id="form" class="form-horizontal">                        <div class="form-group">                            <label class="col-sm-2 control-label">名称</label>                            <div class="col-sm-10">                                <input type="text" class="form-control" name="carousel_name">                            </div>                        </div>                        <div class="hr-line-dashed"></div>                        <div class="form-group">                            <label class="col-sm-2 control-label">图片</label>                            <div class="col-sm-10">                                <input type="file" class="form-control" name="image">                            </div>                        </div>                        <div class="hr-line-dashed"></div>                        <div class="form-group">                            <label class="col-sm-2 control-label">排序</label>                            <div class="col-sm-10">                                <input type="text" class="form-control" name="sort">                            </div>                        </div>                        <div class="hr-line-dashed"></div>                        <div class="form-group">                            <label class="col-sm-2 control-label">是否显示</label>                            <div class="radio">                                <label>                                    <input type="radio" checked="" value="1" name="is_show">是</label>                                <label>                                    <input type="radio" checked="" value="0" name="is_show">否</label>                            </div>                        </div>                        <div class="hr-line-dashed"></div>                        <div class="form-group">                            <div class="col-sm-4 col-sm-offset-2">                                <button class="btn btn-primary" type="button">保存</button>                                <button class="btn btn-white" type="reset">重置</button>                            </div>                        </div>                        {{ csrf_field() }}                    </form>                </div>            </div>        </div>    </div></div><!-- 全局js --><script src="js/jquery.min.js?v=2.1.4"></script><script src="js/bootstrap.min.js?v=3.3.6"></script><!-- 自定义js --><script src="js/content.js?v=1.0.0"></script><!-- iCheck --><script src="js/plugins/iCheck/icheck.min.js"></script><script>    $(document).ready(function () {        $('.i-checks').iCheck({            checkboxClass: 'icheckbox_square-green',            radioClass: 'iradio_square-green',        });    });    $('.btn-primary').click(function(){        var file = new FormData(document.getElementById('form'));        var url = "{{ url('carouselCarousel') }}";        $.ajax({            url:url,            type:'post',            data:file,//            data:$('#form').serialize(),            dataType:'json',            processData:false,            contentType:false,            success:function(msg){                if (msg.success = 1) {                    if (msg.data.is_show == 1) {                        var is_show = '<img src="img/yes.gif" class="image">';                    } else {                        var is_show = '<img src="img/no.gif" class="image">';                    }                       var str = '<tr><td>'+msg.data.carousel_name+'</td>';                       str += '<td><img src="upload/'+msg.data.carousel_path+'" alt="" style="height: 118px;width: 200px"></td>';                       str += '<td>'+msg.data.sort+'</td>';                       str += '<td>'+is_show+'</td>';                       str += '<td><a href="{{ url('carouselDelete') }}" onclick="if (confirm(\'确实要删除吗？\') == false) return false" id="delete" alt="'+msg.data.carousel_id+'">删除</a></td></tr>';                    $('#tbody').append(str);                }            }        });    })    //删除    $('tbody').delegate('#delete', 'click', function(){        var obj = $(this);        var id = obj.attr('alt');        $.get("{{ url('carouselDelete') }}", {id:id}, function(msg){            if (msg == 1) {                obj.parent().parent().remove();            }        });    })</script></body></html>