><!-- Peity -->{{--<script src="js/demo/peity-demo.js"></script>--}}<script>    $(document).ready(function () {        $('.i-checks').iCheck({            checkboxClass: 'icheckbox_square-green',            radioClass: 'iradio_square-green',        });    });    //搜索    $('#search').click(function(){        page(1)    })    function page(p)    {        $('#hidden').val(p);        var file = new FormData(document.getElementById('searchForm'));        var url = "{{ url('goodsSearch') }}";        $.ajax({            url:url,            type:'post',            data:file,//            data:$('#searchForm').serialize(),            dataType:'json',            processData:false,            contentType:false,            success:function(msg){//                alert(msg.data)                $('tbody').html(msg.data);                $('#pages').html(msg.page);            }        });    }    //即点即改    $('tbody').delegate('.c_name', 'click', function(){        var obj = $(this).next();        var val = $(this).html();        $(this).hide();        obj.show();        obj.val(val);    })    $('tbody').delegate('.cname', 'blur', function(){        var obj = $(this).prev();        var val = $(this).val();        var name = $(this).attr('name');        var id = $(this).parents('tr').attr('alt');        $(this).hide();        $.post("{{ url('goodsAlter') }}", {id:id, name:name, val:val}, function(msg){            if (msg == 1) {                obj.show();                obj.html(val);            }        });    })    //修改状态值    $('tbody').delegate('.image', 'click', function(){        var obj = $(this);        var img = obj.attr('src');        var id = obj.parents('tr').attr('alt');        var name = obj.parent('td').attr('alt');        if (img == 'img/yes.gif') {            $.post("{{ url('goodsAlter') }}", {flog:0, id:id, name:name}, function(msg){                if (msg == 1) {                    obj.attr('src', 'img/no.gif');                }            })        } else {            $.post("{{ url('goodsAlter') }}", {flog:1, id:id, name:name}, function(msg){                if (msg == 1) {                    obj.attr('src', 'img/yes.gif');                }            })        }    })    //全选 反选    $('#checkAll').click(function(){      $('.ckbox').each(function(){          if ($(this).prop('checked')) {              $(this).prop('checked', false);          } else {              $(this).prop('checked', true);          }      })    });    //批删    $('#deleteAll').click(function(){        $('.ckbox:checked').each(function(){            var obj = $(this);            var id = $(this).val();            del(obj, id);        })    })    //单删    $('.delete').click(function(){        var obj = $(this);        var id = $(this).attr('alt');        del(obj, id);    });    //删除    function del(obj, id)    {        $.get("{{ url('goodsDelete') }}", {id:id}, function(msg){            if (msg == 1) {                obj.parent().parent().remove();            }        });    }</script></body></html>