<!DOCTYPE html><html><head>    <meta name="csrf-token" content="{{ csrf_token() }}">    <base href="{{ asset('./') }}">    <meta charset="utf-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title> - 找回密码</title>    <meta name="keywords" content="">    <meta name="description" content="">    <link rel="shortcut icon" href="favicon.ico"><link href="css/bootstrap.min.css?v=3.3.6" rel="stylesheet">    <link href="css/font-awesome.css?v=4.4.0" rel="stylesheet">    <link href="css/animate.css" rel="stylesheet">    <link href="css/style.css?v=4.1.0" rel="stylesheet">    <!--[if lt IE 9]>    <meta http-equiv="refresh" content="0;ie.html" />    <![endif]-->    <script>if(window.top !== window.self){ window.top.location = window.location;}</script>    <style>        .icon_step {            disabled:disabled;        }    </style></head><body class="gray-bg"><div class="middle-box text-center loginscreen  animated fadeInDown">    <div class="tabs-container" style="width:400px;">        <div style="margin-right: 300px">            <h5 ><a href="{{ url('loginLogin') }}">返回登录</a></h5><br>        </div>        <form action="{{ url('loginReset') }}" method="post">        @if(isset($state) && $state == 1)            <ul class="nav nav-tabs">                <li class="step1 "><a data-toggle="" href="javascript:void(0)" > 输入邮箱地址</a>                </li>                <li class="step2 " ><a data-toggle="" href="javascript:void(0)" >验证邮箱</a>                </li>                <li class="step3 active"><a data-toggle="tab" href="javascript:void(0)">重置密码</a>                </li>            </ul>        @else            <ul class="nav nav-tabs">                <li class="step1 active"><a data-toggle="tab" href="javascript:void(0)" > 输入邮箱地址</a>                </li>                <li class="step2 " ><a data-toggle="" href="javascript:void(0)" >验证邮箱</a>                </li>                <li class="step3"><a data-toggle="" href="javascript:void(0)">重置密码</a>                </li>            </ul>        @endif        <div class="tab-content">            @if(isset($state) && $state == 1)                <div id="tab-1" class="tab-pane ">            @else               <div id="tab-1" class="tab-pane active">            @endif                <div class="panel-body">                    <div class="form-group">                        <input type="text" class="form-control" placeholder="请输入注册时使用的邮箱地址" id= "email" >                    </div>                    <button type="button" class="btn btn-primary block full-width reset">找回密码</button>                </div>            </div>            <div id="tab-2" class="tab-pane ">                <div class="panel-body">                    <h3 class="reset_mail">密码重置邮件已发送至你的邮箱：<span class="des_mail"></span></h3>                    <p class="reset_tips">请在24小时内登录你的邮箱接收邮件,链接激活后可重置密码。</p>                    <button type="button" class="btn btn-primary block full-width" id="email_url">登录邮箱查看</button>                </div>            </div>            @if(isset($state) && $state == 1)                <div id="tab-3" class="tab-pane active">            @else                <div id="tab-3" class="tab-pane">            @endif                <div class="panel-body">                    <div class="form-group">                        <input type="password" class="form-control" placeholder="请输入新密码" name="password" id= "password" >                    </div>                    <div class="form-group">                        <input type="password" class="form-control" placeholder="请再次输入新密码" name="pwd" id= "pwd" >                    </div>                    @if(isset($state))                    <input type="hidden" name="email" value="{{ $email }}" id="email_val">                    @endif                    <button type="submit" class="btn btn-primary block full-width submit">确定</button>                </div>            </div>        </div>                {{ csrf_field() }}        </form>    </div></div><!-- 全局js --><script src="js/jquery.min.js?v=2.1.4"></script><script src="js/bootstrap.min.js?v=3.3.6"></script><!-- 自定义js --><script src="js/content.js?v=1.0.0"></script><script>    $.ajaxSetup({        headers: {            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')        }    });</script><script>    $('.reset').click(function(){        email = $('#email').val();        var obj = $(this);        $.post("{{ url('loginEmail') }}", {email:email}, function(msg){            if (msg.success == 1) {                $('.step1').removeClass('active');                $('.step1 a').attr('data-toggle', '');                $('#tab-1').removeClass('active');                $('.step2').addClass('active');                $('.step2 a').attr('data-toggle', 'tab');                $('#tab-2').addClass('active');                $('.des_mail').html(email);                $('#email_val').val(msg.data);            } else {                obj.before('<span><font color="red">请输入有效的邮箱地址</font></span>');            }        },'json');    })    //登录邮箱    $('#email_url').click(function(){        location.href='https://mail.qq.com';    })    //重置密码    $('.submit').click(function(){        var password = $('#password').val();        var pwd = $('#pwd').val();        var reg = /\w{6,18}/;        if (reg.test(password)) {            if (password != pwd) {                $(this).before('<span><font color="red">两次密码不同</font></span>');                return false;            }        } else {            $(this).before('<span><font color="red">密码为6-18位</font></span>');            return false;        }    })</script></body></html>