ndif                                            @endforeach                                            >{{ $val['node_title'] }}</label>                                        @foreach($val['child'] as $v)                                            <label class="checkbox-inline">                                            <input type="checkbox" value="{{ $v['node_id'] }}" id="inlineCheckbox1" name="node_id[]"                                            @foreach($role['nodes'] as $value)                                                @if($v['node_id'] == $value['node_id'])                                                    {{ 'checked' }}                                                @endif                                            @endforeach                                            >{{ $v['node_title'] }}</label>                                        @endforeach                                    @endforeach                                </div>                        </div>                        <div class="hr-line-dashed"></div>                        <div class="form-group">                            <div class="col-sm-4 col-sm-offset-2">                                <button class="btn btn-primary" type="submit">赋权</button>                                <button class="btn btn-white" type="reset">重置</button>                            </div>                        </div>                        {{ csrf_field() }}                    </form>                </div>            </div>        </div>    </div></div><!-- 全局js --><script src="js/jquery.min.js?v=2.1.4"></script><script src="js/bootstrap.min.js?v=3.3.6"></script><!-- 自定义js --><script src="js/content.js?v=1.0.0"></script><!-- iCheck --><script src="js/plugins/iCheck/icheck.min.js"></script><script>    $(document).ready(function () {        $('.i-checks').iCheck({            checkboxClass: 'icheckbox_square-green',            radioClass: 'iradio_square-green',        });    });</script></body></html>